// Extract email details and prepare for AI processing
const inputData = $input.first().json;

// Safely extract sender name from email address
let senderName = 'Customer';
if (inputData.from && typeof inputData.from === 'string') {
  if (inputData.from.includes('<')) {
    senderName = inputData.from.split('<')[0].trim().replace(/"/g, '');
  } else {
    senderName = inputData.from.split('@')[0];
  }
} else if (inputData.sender && typeof inputData.sender === 'string') {
  senderName = inputData.sender.split('@')[0];
}

const emailData = {
  from: inputData.from || inputData.sender || 'unknown@email.com',
  to: inputData.to || inputData.recipient || '',
  subject: inputData.subject || 'No Subject',
  body: inputData.textPlain || inputData.textHtml || inputData.body || inputData.snippet || '',
  messageId: inputData.id || inputData.messageId || 'no-id',
  timestamp: new Date().toISOString(),
  senderName: senderName
};

// Clean the email body for AI processing
let cleanBody = emailData.body
  .replace(/\n\s*\n/g, '\n')  // Remove multiple line breaks
  .replace(/^On .* wrote:$/gm, '')  // Remove email thread headers
  .replace(/^>.*$/gm, '')  // Remove quoted text
  .trim();

// Check for escalation keywords
const escalationKeywords = ($vars.ESCALATION_KEYWORDS || 'project,custom,quote').toLowerCase().split(',');
const shouldEscalate = escalationKeywords.some(keyword => 
  cleanBody.toLowerCase().includes(keyword.trim()) ||
  emailData.subject.toLowerCase().includes(keyword.trim())
);

return {
  json: {
    ...emailData,
    cleanBody,
    shouldEscalate,
    escalationReason: shouldEscalate ? 'Contains escalation keywords' : null
  }
};

