// Parse and analyze email with OpenAI

// 1. Get email data
const emailSubject = $json.subject || $json.payload?.headers?.find(h => h.name === 'Subject')?.value || 'No subject';
let emailBody = 'No body';
if ($json.payload?.parts) {
  const textPart = $json.payload.parts.find(p => p.mimeType === 'text/plain');
  if (textPart?.body?.data) {
    emailBody = Buffer.from(textPart.body.data, 'base64').toString('utf-8');
  }
} else if ($json.snippet) {
  emailBody = $json.snippet.replace(/{{[^}]+}}/g, '').trim();
}

// 2. Define OpenAI prompt
const prompt = `Categorize the following email and provide a brief summary.
Categories:
- escalation_needed
- automated_reply

Email Subject: ${emailSubject}
Email Body: ${emailBody}`;

// 3. Make OpenAI API call
const response = await this.helpers.httpRequest({
  url: 'https://api.openai.com/v1/chat/completions',
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${$vars["OPENAI_API_KEY"]}`,
    'Content-Type': 'application/json'
  },
  body: {
    model: 'gpt-4o',
    messages: [{ role: 'user', content: prompt }]
  }
});

// 4. Extract and return analysis
const aiResponse = response.choices[0].message.content;
return [{
  json: {
    originalEmailSubject: emailSubject,
    originalEmailBody: emailBody,
    aiAnalysis: aiResponse
  }
}];
